function initializeButtons(){document.getElementById("btn-sampler").onclick=()=>{navigateToTool("sampler")},document.getElementById("btn-terminology").onclick=()=>{navigateToTool("terminology")},document.getElementById("btn-client").onclick=()=>{navigateToTool("client")}}function navigateToTool(e){console.log(`Navigating to tool: ${e}`);const o=window.location.origin+window.location.pathname.replace("launcher.html","");let n;switch(e){case"sampler":n=o.replace("/launcher/","/sampler/")+"sampler.html";break;case"terminology":n=o.replace("/launcher/","/terminologie/")+"terminologie.html";break;case"client":n=o.replace("/launcher/","/klient/")+"klient.html";break;default:return void console.error("Unknown tool:",e)}window.location.href=n}function showError(e){console.error(e),alert(e)}Office.onReady(e=>{e.host===Office.HostType.Excel&&(document.getElementById("sideload-msg").style.display="none",document.getElementById("app-body").classList.remove("hidden"),initializeButtons())});